package com.example.prashantmaheshwari.firstapp;import android.content.Intent;import android.os.Bundle;import android.support.v7.app.AppCompatActivity;import android.view.View;import android.widget.AdapterView;import android.widget.ListView;import com.google.firebase.auth.FirebaseAuth;import com.google.firebase.auth.FirebaseUser;import com.google.firebase.database.DatabaseReference;import com.google.firebase.database.FirebaseDatabase;import java.util.ArrayList;import java.util.List;public class Extras extends AppCompatActivity{    List<Works> extrasList;    ListView listView;    private FirebaseUser user;    private DatabaseReference database;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_extras);        extrasList = new ArrayList<Works>();        listView = (ListView) findViewById(R.id.listview);        user = FirebaseAuth.getInstance().getCurrentUser();        String text = user.getUid();        database = FirebaseDatabase.getInstance().getReference(text+"/Extras");        extrasList.add(new Works(R.drawable.rihanna, "Need Towing", "1200.00"));        extrasList.add(new Works(R.drawable.rihanna, "Key Locked Inside", "599.00"));        extrasList.add(new Works(R.drawable.rihanna, "Dead Battery", "599.00"));        extrasList.add(new Works(R.drawable.rihanna, "Empty Tank", "599.00"));        extrasList.add(new Works(R.drawable.rihanna, "Flat Tyre", "599.00"));        extrasList.add(new Works(R.drawable.rihanna, "Accident Issue", "1000.00"));        extrasList.add(new Works(R.drawable.rihanna, "Heating Issue", "500.00"));        extrasList.add(new Works(R.drawable.rihanna, "Poor Breaking", "800.00"));        extrasList.add(new Works(R.drawable.rihanna, "Lights Problem", "1100.00"));        extrasList.add(new Works(R.drawable.rihanna, "Other Problem", "2000.00"));        CustomListView adapter = new CustomListView(this, R.layout.list_row, extrasList);        listView.setAdapter(adapter);        listView.setOnItemClickListener(new AdapterView.OnItemClickListener() {            @Override            public void onItemClick(AdapterView<?> adapterView, View view, int i, long l) {                Works work = extrasList.get(i);                String w = work.getTitle();                database.child("title").setValue(w);                String m = work.getMoney();                database.child("money").setValue(m);                startActivity(new Intent(Extras.this,FinalPage.class));            }        });    }}